SET DEFINE OFF

CREATE TABLE MAIN_MOVIE ( movie_id VARCHAR2(16),
    homepage VARCHAR2(256),
    original_title VARCHAR2(128),
    overview VARCHAR2(4000),
    popularity NUMBER(16, 6),
    revenue NUMBER(16),
