INSERT INTO MAIN_MOVIE (movie_id, homepage, original_title, overview, popularity, revenue, runtime, status, tagline, title, vote_average, vote_count, budget, release_date, original_language) 
VALUES ('2454', '', 'The Chronicles of Narnia: Prince Caspian', 'One year after their incredible adventures in the Lion, the Witch and the Wardrobe, Peter, Edmund, Lucy and Susan Pevensie return to Narnia to aid a young prince whose life has been threatened by the evil King Miraz. Now, with the help of a colorful cast of new characters, including Trufflehunter the badger and Nikabrik the dwarf, the Pevensie clan embarks on an incredible quest to ensure that Narnia is returned to its rightful heir.', 53.978602, 4.19651413E8, 150.0, 'Released', 'Hope has a new face.', 'The Chronicles of Narnia: Prince Caspian', 6.3, 1630.0, 2.25E8, to_date('2008-05-15', 'RRRR-MM-DD'), 'en');

