INSERT INTO MAIN_MOVIE (movie_id, homepage, original_title, overview, popularity, revenue, runtime, status, tagline, title, vote_average, vote_count, budget, release_date, original_language) 
VALUES ('1865', 'http://disney.go.com/pirates/index-on-stranger-tides.html#/video/', 'Pirates of the Caribbean: On Stranger Tides', 'Captain Jack Sparrow crosses paths with a woman from his past, and he''s not sure if it''s love -- or if she''s a ruthless con artist who''s using him to find the fabled Fountain of Youth. When she forces him aboard the Queen Anne''s Revenge, the ship of the formidable pirate Blackbeard, Jack finds himself on an unexpected adventure in which he doesn''t know who to fear more: Blackbeard or the woman from his past.', 135.413856, 1.045713802E9, 136.0, 'Released', 'Live Forever Or Die Trying.', 'Pirates of the Caribbean: On Stranger Tides', 6.4, 4948.0, 3.8E8, to_date('2011-05-14', 'RRRR-MM-DD'), 'en');

INSERT INTO MAIN_MOVIE (movie_id, homepage, original_title, overview, popularity, revenue, runtime, status, tagline, title, vote_average, vote_count, budget, release_date, original_language) 
VALUES ('41154', 'http://www.sonypictures.com/movies/meninblack3/', 'Men in Black 3', 'Agents J (Will Smith) and K (Tommy Lee Jones) are back...in time. J has seen some inexplicable things in his 15 years with the Men in Black, but nothing, not even aliens, perplexes him as much as his wry, reticent partner. But when K''s life and the fate of the planet are put at stake, Agent J will have to travel back in time to put things right. J discovers that there are secrets to the universe that K never told him - secrets that will reveal themselves as he teams up with the young Agent K (Josh Brolin) to save his partner, the agency, and the future of humankind.', 52.035179, 6.24026776E8, 106.0, 'Released', 'They are back... in time.', 'Men in Black 3', 6.2, 4160.0, 2.25E8, to_date('2012-05-23', 'RRRR-MM-DD'), 'en');

INSERT INTO MAIN_MOVIE (movie_id, homepage, original_title, overview, popularity, revenue, runtime, status, tagline, title, vote_average, vote_count, budget, release_date, original_language) 
VALUES ('122917', 'http://www.thehobbit.com/', 'The Hobbit: The Battle of the Five Armies', 'Immediately after the events of The Desolation of Smaug, Bilbo and the dwarves try to defend Erebor''s mountain of treasure from others who claim it: the men of the ruined Laketown and the elves of Mirkwood. Meanwhile an army of Orcs led by Azog the Defiler is marching on Erebor, fueled by the rise of the dark lord Sauron. Dwarves, elves and men must unite, and the hope for Middle-Earth falls into Bilbo''s hands.', 120.965743, 9.56019788E8, 144.0, 'Released', 'Witness the defining chapter of the Middle-Earth saga', 'The Hobbit: The Battle of the Five Armies', 7.1, 4760.0, 2.5E8, to_date('2014-12-10', 'RRRR-MM-DD'), 'en');

INSERT INTO MAIN_MOVIE (movie_id, homepage, original_title, overview, popularity, revenue, runtime, status, tagline, title, vote_average, vote_count, budget, release_date, original_language) 
VALUES ('1930', 'http://www.theamazingspiderman.com', 'The Amazing Spider-Man', 'Peter Parker is an outcast high schooler abandoned by his parents as a boy, leaving him to be raised by his Uncle Ben and Aunt May. Like most teenagers, Peter is trying to figure out who he is and how he got to be the person he is today. As Peter discovers a mysterious briefcase that belonged to his father, he begins a quest to understand his parents'' disappearance – leading him directly to Oscorp and the lab of Dr. Curt Connors, his father''s former partner. As Spider-Man is set on a collision course with Connors'' alter ego, The Lizard, Peter will make life-altering choices to use his powers and shape his destiny to become a hero.', 89.866276, 7.52215857E8, 136.0, 'Released', 'The untold story begins.', 'The Amazing Spider-Man', 6.5, 6586.0, 2.15E8, to_date('2012-06-27', 'RRRR-MM-DD'), 'en');

