INSERT INTO MAIN_MOVIE (movie_id, homepage, original_title, overview, popularity, revenue, runtime, status, tagline, title, vote_average, vote_count, budget, release_date, original_language) 
VALUES ('597', 'http://www.titanicmovie.com', 'Titanic', '84 years later, a 101-year-old woman named Rose DeWitt Bukater tells the story to her granddaughter Lizzy Calvert, Brock Lovett, Lewis Bodine, Bobby Buell and Anatoly Mikailavich on the Keldysh about her life set in April 10th 1912, on a ship called Titanic when young Rose boards the departing ship with the upper-class passengers and her mother, Ruth DeWitt Bukater, and her fiancé, Caledon Hockley. Meanwhile, a drifter and artist named Jack Dawson and his best friend Fabrizio De Rossi win third-class tickets to the ship in a game. And she explains the whole story from departure until the death of Titanic on its first and last voyage April 15th, 1912 at 2:20 in the morning.', 100.025899, 1.845034188E9, 194.0, 'Released', 'Nothing on Earth could come between them.', 'Titanic', 7.5, 7562.0, 2.0E8, to_date('1997-11-18', 'RRRR-MM-DD'), 'en');

